# Check required addresses
# Safe Singleton Factory
code=$(cast code 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7 --rpc-url "$RPC_URL")
if [ "$code" == "0x" ]; then
    printf '%s\n' "Error: Safe Singleton Factory not deployed" >&2
    exit 1
fi

# Counter 
code=$(cast code 0x19575934a9542be941d3206f3ecff4a5ffb9af88 --rpc-url "$RPC_URL")
if [ "$code" == "0x" ]; then
    cast send 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7  --rpc-url "$RPC_URL" --private-key "$PRIVATE_KEY"
fi
printf '\e]8;;https://contractscan.xyz/contract/0x19575934a9542be941d3206f3ecff4a5ffb9af88\e\\Counter successfully deployed\e]8;;\e\\\n'
